package com.example.adapter;import java.util.Map;import com.google.gson.Gson;import com.google.gson.GsonBuilder;import com.google.gson.reflect.TypeToken;public class AsyJsonData {	public Map<?, ?> parseData(String data) {		GsonBuilder gb = new GsonBuilder();		Gson g = gb.create();		Map<?, ?> map = g.fromJson(data, new TypeToken<Map<?, ?>>() {		}.getType());		return map;	}}